local url="https://api.github.com/repos/thequomy/anims/contents"local scs,response=pcall(function()return game:HttpGet(url)end)if not scs then warn("error "..tostring(response))return end;local files=game:GetService("HttpService"):JSONDecode(response)local anims={}for _,file in ipairs(files)do if file.name:match("%.lua$")then local name=file.name:gsub("%%20"," "):gsub("%.lua$","")anims[name]="https://github.com/thequomy/anims/raw/main/"..file.name end end;return anims
