local HttpService=game:GetService("HttpService")local url="https://api.github.com/repos/thequomy/anims/git/trees/main?recursive=1"local success,response=pcall(function()return game:HttpGet(url)end)if not success then warn(tostring(response))return end;local data=HttpService:JSONDecode(response)local anims={}for _,item in ipairs(data.tree)do if item.path:match("%.lua$")then local name=item.path:gsub("%.lua$","")anims[name]="https://github.com/thequomy/anims/raw/main/"..item.path end end;return anims
